def part1():
    floor = 0
    for c in data:
        if c == '(':
            floor += 1
        else:
            floor -= 1
    return floor

def part2():
    floor = 0
    for i, c in enumerate(data, start=1):
        if c == '(':
            floor += 1
        else:
            floor -= 1
        if floor < 0:
            return i


print(part1())
